import React from 'react'
import { View, StyleSheet, Button, Alert } from "react-native";

const Confirmation = (props) =>
    Alert.alert(
        props.title,
        props.children,
        [
            {
                text: "Cancel",
                onPress: () => props.handleConfirmationMessage('no'),
                style: "cancel"
            },
            { text: "OK", onPress: () => props.handleConfirmationMessage('yes') }
        ],
        { cancelable: false }
    );
export default Confirmation
