import React, { Component } from "react";
import { View, ScrollView, Text, TouchableOpacity } from "react-native";
import { widthPercentageToDP as wp } from "react-native-responsive-screen";
import Icon from "@expo/vector-icons/Ionicons";
import BasketItem from "../components/BasketItem";
import BasketTotalList from "../components/BasketTotalList";

class Basket extends Component {
  constructor(props) {
    super(props)
  }

  getItems = () => {
    if (Object.keys(props.cart).length > 0) {
      return Object.keys(props.cart.items).map((i) => {
        return <BasketItem
          editIcon={true}
          imageUri={props.cart.items[i].item.imagePath}
          name={props.cart.items[i].item.title}
          color={props.cart.items[i].item.color}
          size={props.cart.items[i].item.size}
          price={120}
          {...this.props}
        />
      })
    }
  }

  render() {
    return (
      <View style={{ flex: 1, backgroundColor: "#EFF0F1" }}>
        {/* ItemLists_upper */}
        <View style={{ flex: 2 }}>
          <ScrollView>
            {this.getItems()}

          </ScrollView>
        </View>
        {/* ItemLists_upper */}
        {/* total_lower */}
        <View style={{ flex: 1, paddingTop: wp("10%") }}>
          <BasketTotalList label="Shipping" price={6} />
          <BasketTotalList label="Your total" price={380} />
          <View style={{ flex: 1, paddingHorizontal: 20, justifyContent: "flex-end", paddingBottom: 15 }}>
            <TouchableOpacity
              activeOpacity={0.8}
              onPress={() => this.props.navigation.navigate("Checkout")}
              style={{
                flexDirection: "row",
                backgroundColor: "#F08C4F",
                justifyContent: "center",
                alignItems: "center",
                borderRadius: 2,
                shadowOffset: { width: 1, height: 2 },
                shadowColor: "#000",
                shadowOpacity: 0.4,
                elevation: 4,
                paddingVertical: 10
              }}
            >
              <View style={{ marginRight: 15 }}>
                <Icon name="md-cart" size={20} color="white" />
              </View>
              <Text style={{ fontSize: 18, fontWeight: "500", color: "white" }}>
                Place your order
              </Text>
            </TouchableOpacity>
          </View>
        </View>
        {/* total_lower */}
      </View>
    );
  }
}

export default Basket;

