import React, { Component } from "react";
import {
  View,
  Text,
  ScrollView,
  Image,
  Dimensions,
  TouchableOpacity,
  Animated,
  TouchableWithoutFeedback
} from "react-native";
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp
} from "react-native-responsive-screen";
import Icon from "@expo/vector-icons/Ionicons";
import StarRating from "react-native-star-rating";
import ChoosingSizeBox from "../components/ChoosingSizeBox";

const { width } = Dimensions.get("window");

class Detail extends Component {
  state = {
    defaultBox: null,
    size: "small",
    color: "black",
    sizeBoxOpen: false,
    colorBoxOpen: false,
    colorIconName: "ios-arrow-down",
    iconName: "ios-arrow-down",
    sizeBorderColor: "gray",
    colorBorderColor: "gray"
  };

  componentWillMount() {
    this.sizeBox = new Animated.Value(hp("65%"));
    this.colorBox = new Animated.Value(hp("65%"));
  }

  onChooseItem = item => {
    this.setState({ size: item });
  };

  onChooseColor = item => {
    this.setState({ color: item });
  };

  openColorBox = () => {
    this.setState(
      (prevState, props) => {
        return {
          sizeBoxOpen: false,
          colorBoxOpen: !prevState.colorBoxOpen,
          colorIconName:
            prevState.colorIconName === "ios-arrow-down"
              ? "ios-arrow-up"
              : "ios-arrow-down",
          iconName: "ios-arrow-down",
          colorBorderColor:
            prevState.colorBorderColor === "gray" ? "black" : "gray",
          sizeBorderColor: "gray",
          defaultBox: "colorBox"
        };
      },
      () => {
        if (this.state.colorBoxOpen) {
          Animated.timing(this.colorBox, {
            toValue: hp("30%"),
            duration: 400
          }).start();
        } else {
          Animated.timing(this.colorBox, {
            toValue: hp("65%"),
            duration: 400
          }).start();
        }
        if (this.state.sizeBoxOpen) {
          Animated.timing(this.sizeBox, {
            toValue: hp("30%"),
            duration: 400
          }).start();
        } else {
          Animated.timing(this.sizeBox, {
            toValue: hp("65%"),
            duration: 400
          }).start();
        }
      }
    );
  };

  openSizeBox = () => {
    this.setState(
      (prevState, props) => {
        return {
          colorBoxOpen: false,
          sizeBoxOpen: !prevState.sizeBoxOpen,
          iconName:
            prevState.iconName === "ios-arrow-down"
              ? "ios-arrow-up"
